INSERT INTO `ads` (`id_ads`, `name_ads`, `text_ads`, `date`) VALUES
	(1, 'Прокат зимнего спортивного инвентаря', '<p>На базе физкультурно-оздоровительного центра работает прокат коньков и лыж c 10:00 до 20:00:</p>\r\n<ul>\r\n	<li>1. прокат коньков 1.60 руб./час; 1 руб. каждый последующий час;</li>\r\n	<li>2. прокат лыж 1.60 руб./час; 1 руб. каждый последующий час;</li>\r\n	<li>3. покататься на своих коньках 0.50 руб.</li>\r\n</ul>', '2017-01-10'),
	(2, 'Турнир по греко-римской борьбе', '<p>14 января 2017 года (суббота) состоится открытый городской турнир по греко-римской борьбе среди юношей 2005-2007 г.р., на призы ГУ «ФОЦ «Костюковка-спорт».</p>\r\n<p>Всех желающих просим прийти на данное мероприятие. Начало в 12:00</p>', '2017-01-11'),
	(3, 'вриапирапир', '<p>14 января 2017 года (суббота) состоится открытый городской турнир по греко-римской борьбе среди юношей 2005-2007 г.р., на призы ГУ «ФОЦ «Костюковка-спорт».</p>\r\n<p>Всех желающих просим прийти на данное мероприятие. Начало в 12:00</p>', '2017-01-05'),
	(4, 'Дата', '<p>Посмотрим сейчас!!!</p>\r\n', '2017-05-03');
