INSERT INTO `coach` (`id_coach`, `name`, `name_en`, `id_sport_section`, `description`, `archive`) VALUES
	(1, 'Веретенников Вадим Витальевич', 'veretennikov', 1, '- 1979 года рождения. Заместитель директора СДЮШОР, образование высшее (окончил Академию физического воспитания и спорта РБ в 2002 году), работает в СДЮШОР с 1998 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК.  Присвоена первая тренерско-преподавательская категория и первая категория судья по спорту.', NULL),
	(2, 'Чуешков Сергей Петрович', 'chueshkov', 1, '- 1975 года рождения.  Образование высшее (окончил Академию физического воспитания и спорта РБ в 2002 году), работает в СДЮШОР с 2002 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК. Присвоена первая тренерско-преподавательская категория и первая категория судья по спорту.', NULL),
	(3, 'Волчун Вероника Валерьевна', 'volchun', 1, '- 1992 года рождения. Образование среднее специальное (окончила Гомельское училище олимпийского резерва в 2011 году), на данный момент студентка Гомельского университета им.Ф.Скарины. Работает в СДЮШОР с 2010 года. Присвоена вторая тренерско-преподавательская категория и первая категория судья по спорту.', NULL),
	(4, 'Копытов Владимир Николаевич', 'kopytov', 2, '- 1965 года рождения. Высшее физкультурное образование. Заслуженный мастер спорта Республики Беларусь по греко-римской борьбе, тренер-преподаватель высшей категории. Работает в СДЮШОР с 2002 года.', NULL),
	(5, 'Варивода Александр Алексеевич', 'varivoda', 2, '- 1979 года рождения. Высшее физкультурное образование. Кандидат в мастера спорта по греко-римской борьбе, тренер-преподаватель высшей категории, судья международной категории. Работает в СДЮШОР с 1998 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК.', NULL),
	(6, 'Клеменцев Павел Александрович', 'klementsev', 2, '- 1982 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2002 года.', NULL),
	(7, 'Клеменцев Дмитрий Владимирович', 'klementsev_dm', 2, '- 1990 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2010 года.', NULL),
	(8, 'Старовойтова Людмила Викторовна', 'starovoytova', 3, '- 1974 года рождения. Высшее физкультурное образование. Тренер-преподаватель первой категории по легкой атлетике. Работала в СДЮШОР с 1998 г. по 2016 г.', 'archive'),
	(9, 'Куприянов Виталий Александрович', 'kupriyanov', 3, '- 1973 года рождения. Высшее физкультурное образование. Тренер-преподаватель высшей категории по легкой атлетике. Работает в СДЮШОР с 1998 года.', NULL),
	(10, 'Кравченко Руслан Викторович', 'kravchenko', 4, '- 1982 года рождения. Мастер спорта РБ по тяжелой атлетике. Среднее специальное физкультурное образование, тренер-преподаватеь высшей категории. Работает в СДЮШОР с 2004 года.', NULL),
	(11, 'Подлобников Максим Александрович', 'podlobnikov', 4, '- 1994 года рождения. Мастер спорта по тяжелой атлетике. Среднее специальное физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2014 года.', NULL),
	(12, 'Блашкевич Дмитрий Владимирович', 'blashkevich', 5, '- 1974 года рождения. Высшее физкультурное образование. Тренер-преподаватель по футболу первой категории. Работает в СДЮШОР с 1996 года. В 2015 году прошел курсы повышения квалификации в ИППК БГУФК.', NULL),
	(13, 'Петрушенко Наталья Ильинична', 'petrishina', 6, '- 1985 г.р. Тренер-преподаватель «СДЮШОР ППО ОАО «Гомельстекло» по волейболу. Образование высшее – закончила ГГУ им.Ф.Скарины в 2010 году. Вторая тренерская категория. Работает в СДЮШОР с февраля 2015 года.', NULL),
	(14, 'Котоликов Сергей Алексеевич', 'katolikov', 3, '- 1985 года рождения. Высшее физкультурное образование. Тренер-преподаватель первой категории по легкой атлетике. Работает в СДЮШОР с 2004 года.', NULL),
	(15, 'Клеменцева Дарья Евгеньевна', 'klementseva', 3, '- 1992 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории по легкой атлетике. Работает в СДЮШОР с 2016 года.', NULL);
INSERT INTO `events` (`id_events`, `name_events`, `photo`, `text_events`, `date`) VALUES
	(1, 'Открытое первенство по плаванию', '', '<!-- ФОТО --><!-- КОНЕЦ ФОТО --><!-- КОНТЕНТ -->\r\n<p><strong>Открытие первенства учреждения &quot;СДЮШОР ППО ОАО &quot;Гомельстекло&quot; по плаванию.</strong></p>\r\n\r\n<p>Павел Чайков - Мастер спорта РБ. Призер открытого чемпионата Республики Беларусь по плаванию на дистанции 200 м спина.</p>\r\n\r\n<p>Дарья Бойчук - победительница на дистанции 100 м спина, 3 место на дистанции 200 м кпл.</p>\r\n<!-- КОНЕЦ КОНТЕНТ -->', '2016-12-30'),
	(4, 'Еще один мастер спорта', NULL, '<!-- КОНТЕНТ -->\r\n    <p>В период с 9 ноября по 12 ноября 2016 года в г. Минске бассейне СОК «Трактор»\r\n        прошел открытый чемпионат Республики Беларусь по плаванию памяти заслуженного тренера и врача БССР И.В. Дурейко.\r\n        Наши выпускники: Павел Чайков – ныне студент УО «БГУФК» на дистанции 200 м спина занял третье место.\r\n        Дмитрий Новиков – учащийся УО «Гомельского училища олимпийского резерва –\r\n        остался в шаге от пьедестала четвертое место на дистанции 200м в/стиль.\r\n        Порадовала Дарья Борош хоть и, не пробившись в финал, но преодолев   дистанцию 100м спина с результатом\r\n        1.05.70 сек. показала норматив мастера спорта Республики Беларусь.\r\n        Поздравляем наших ребят с удачным выступлением, здоровья и больших успехов в дальнейшей плавательной карьере.</p>\r\n    <!-- КОНЕЦ КОНТЕНТ -->', '2016-11-15'),
	(5, '«Веселый дельфин»- 2016', NULL, '<p>Хотелось бы отметить и представителей юного поколения: на прошедшем первенстве Гомельской области по плаванию\r\n        в программе «Веселый дельфин», который прошел в период с 16 по 18 ноября в городе Гомеле в бассейне «СДЮШОР» -\r\n        7, наши учащиеся девушки показали неплохие результаты. Валерия Портенкова выполнила первый взрослый разряд\r\n        на дистанции – 200м комплексное плавание, Дарья Бойчук – заняла второе место на дистанции 100м спина, тем самым\r\n        отобравшись в команду Гомельской области на первенство Республики Беларусь, которая пройдет в городе Гродно с\r\n        20 декабря по 23 декабря 2016 года.  Молодцы! Так держать!</p>', '2016-11-20'),
	(6, 'Тяжелая атлетика', NULL, '<p>В городе Бобруйске в период с 01 по 04 ноября   прошла Республиканская спартакиада среди ДЮСШ по тяжелой\r\n        атлетике, наш выпускник Голобурда Никита в в/к до 56 кг, с суммой 162 кг в (рывок 69кг; толчок 93 кг)\r\n        завоевал первое место. Выпускник проходит подготовку в УО «Мозырьское училище олимпийского резерва».\r\n        Администрация учреждения поздравляет Никиту с данным успехом.', '2016-11-10'),
	(7, 'Ура! Я наверное решил эту задачу.', NULL, '<p><strong><em>Да прибутет со мной сила</em></strong> в обучении классного, с огромными возможностями PHP-фреймворка Yii2.</p>\r\n\r\n<p><input name="Ура" type="button" value="Ура" /></p>\r\n', '2017-02-07'),
	(8, 'Установка flexbox', '/frontend/web/upload/global/1.jpg\r\n', '<p>Наташа. Заслуженный мастер спорта по волейболу</p>\r\n', '2004-04-20');
INSERT INTO `history` (`id_history`, `photo_name`, `alt`, `discription_men`, `where_history`, `string`) VALUES
	(1, 'freidlin.jpg', 'Фрейдлин Борис Моисеевич', 'Первый директор Спортивно-оздоровительного комплекса.\r\n Тренер-преподаватель по классической борьбе. Заслуженный тренер РБ.', 'director_sok', 1),
	(2, 'firer.jpg', 'Фирер Яков Михайлович', 'Второй директор ГУ &laquo;Физкультурно-оздоровительного центра &laquo;\r\nКОСТЮКОВКА-СПОРТ&raquo;.', 'director_sok', 1),
	(3, 'chemarev.jpg', 'Щемарев Валерий Михайлович', 'Первый директор Детской Юношеской Спортивной Школы.', 'director_djussh', 1),
	(4, 'paevsky.jpg', 'Паевский Владимир Георгиевич', 'Директор Специализированной Детско-Юношеской Школы\r\n Олимпийского Резерва ППО ОАО "Гомельстекло".', 'director_djussh', 1),
	(5, 'gulevich.jpg', 'Михаил Гулевич', '<strong>Мастер Спорта СССР по классической борьбе.</strong>\r\n<br/>Многократный чемпион БССР, призер первенства СССР.', 'doska_pocheta_wr', NULL),
	(6, 'metelskiy.jpg', 'Сергей Метельский', '<strong>Первый мастер спорта в коллективе физкультуры\r\n стекольного завода по классической борьбе.</strong>\r\n<br/>Серебряный призер 2-х молодежных игр СССР 1977 года,\r\n многократный чемпион БССР.', 'doska_pocheta_wr', NULL),
	(7, 'kopytov.jpg', 'Владимир Копытов', '<strong>Заслуженный мастер спорта РБ по греко-римской борьбе.</strong>\r\n<br/>Бронзовый призер спартакиады народов СССР,\r\n неоднократный призер Европы и Мира. \r\nУчастник Олимпийских игр в Атланте 1996 (США)\r\n и Сиднее 2000 (Австралия).', 'doska_pocheta_wr', NULL),
	(8, 'bondarev.jpg', 'Бондарев Александр', '<strong>Мастер спорта СССР по греко-римской борьбе.</strong>\r\n<br/>Неоднократный победитель первенств РБ.', 'doska_pocheta_wr', NULL),
	(9, 'yaxyaev.jpg', 'Яхьяев Нух', '<strong>Мастер спорта международного класса по грекоримской борьбе.</strong>\r\n<br/>Серебряный призер первенства СССР и молодежного первенства Евроры.', 'doska_pocheta_wr', NULL),
	(10, 'stas.jpg', 'Праходский Стас', '<strong>Мастер спорта РБ.</strong>\r\n<br/>Неоднократный победитель первенств РБ.\r\n Серебряный призер первенства мира.', 'doska_pocheta_wr', NULL),
	(11, 'ezepenko.jpg', 'Езепенко Иван', '<strong>Мастер сопорта РБ по грекоримской борьбе.</strong>\r\n<br/>Двукратный победитель первенства РБ, участник первенства мира.', 'doska_pocheta_wr', NULL),
	(12, 'klementsev.jpg', 'Клеменцев Павел', '<strong>Мастер спрота по греко-римской борьбе.</strong>\r\n<br/>Победитель первенства РБ.', 'doska_pocheta_wr', NULL),
	(13, 'karpaev.jpg', 'Карпаев Илья', '<strong>Мастер спрота по греко-римской борьбе.</strong>\r\n<br/>Неоднократный победитель первенств РБ среди юношей и юниоров. \r\nУчастник первенства мира.', 'doska_pocheta_wr', NULL),
	(14, 'yakyshevskiy.jpg', 'Якушевский Александр', '<strong>Заслуженный тренер БССР.</strong>\r\n<br/>Тренер, который подготовил не одного победителя\r\n и призера Республиканских и Международных соревнований.', 'doska_pocheta_la', NULL),
	(15, 'nevskiy.jpg', 'Невский Александр', '<strong>Мастер спорта международного класса\r\n по легкой атлетике (многоборье).</strong>\r\n<br/>Член сборной команды СССР, экс.рекордмен по\r\n Зимнему л/атлетическому многоборью, участник Олимпийских игр.', 'doska_pocheta_la', NULL),
	(16, 'serduk.jpg', 'Сердюк Алина', '<strong>Мастер спорта РБ по легкой атлетике (копье).</strong>\r\n<br/>Серебряный призер молодежного первенства Европы,\r\n неоднократная чемпионка Республики Беларусь.', 'doska_pocheta_la', NULL),
	(17, 'fedorenko.jpg', 'Федоренко Татьяна', '<strong>Мастер спорта СССР по легкой атлетике (многоборье).</strong>\r\n<br/>Победительница первенства СССР среди юниорок,\r\n рекордсменка СССР.', 'doska_pocheta_la', NULL),
	(18, 'yatchenko.jpg', 'Ятченко Ирина', '<strong>Заслуженный мастер спорта РБ по легкой атлетике (диск).</strong>\r\n<br/>Бронзовый призер Олимпийских игр: Сидней 2000(Австралия), \r\nАфины 2004(Греция). Участница Олимпийских игр в Атланте 1996(США)\r\n и Пекине 2008(Китай). Чемпионка мира.', 'doska_pocheta_la', NULL),
	(19, 'boldovskaya.jpg', 'Болдовская Надежда', '<strong>Мастер спорта СССР по легкой атлетике (многоборье).</strong>\r\n<br/>Серебряный призер Молодежных игр СССР 1977 г.\r\n Экс рекорсменка среди юниоров.', 'doska_pocheta_la', NULL),
	(20, 'kupriyanova.jpg', 'Куприянова Людмила', '<strong>Мастер спорта международного\r\n класса по легкой атлетике (диск).</strong>\r\n<br/>Неоднократная чемпионка РБ,\r\n бронзовый призер молодежных первенств Европы.', 'doska_pocheta_la', NULL),
